import React, { useState } from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function Navbar() {
    const [showSideBar, setShowSideBar] = useState(false);

    function toggleSideBar() {
        setShowSideBar(!showSideBar);
    }

    return (
        <nav className={`navbar navbar-expand-lg navbar-dark bg-transparent ${showSideBar ? 'active' : ''}`}>
            <div className="container">

                {/* LOGO */}
                <a href="#home" className="navbar-brand fs-4" onClick={toggleSideBar}>
                    <img src="../Images/logo.svg" alt="The Pillar" />
                </a>

                {/* TOGGLE BTN */}
                <button className="navbar-toggler shadow-none border-0" type="button" onClick={toggleSideBar}>
                    <span className="navbar-toggler-icon"></span>
                </button>

                {/* SIDEBAR */}
                <div className={`sidebar offcanvas-start ${showSideBar ? 'show' : ''}`} tabIndex="-1" id="offcanvasNavbar" aria-labelledby="offcanvasNavbarLabel">
                    {/* ... */}
                </div>

                {/* SIDEBAR HEADER */}
                <div className="offcanvas-header text-white border-bottom">
                    {/* ... */}
                </div>

                {/* SIDEBAR BODY */}
                <div className="offcanvas-body d-flex flex-column flex-lg-row p-4">
                    {/* ... */}
                </div>

                {/* LOGIN */}
                <div className="d-flex flex-column justify-content-center align-items-center gap-3 flex-lg-row">
                    <a href="#login" className="text-white">Login</a>
                </div>

            </div>
        </nav>
    );
}

export default Navbar;
