import React, { useRef } from 'react';
import video from '../assets/Images/Home_vid.mp4';

const Home = () => {
  const videoRef = useRef(null);

  const playVideo = () => {
    if (videoRef.current) {
      videoRef.current.play();
    }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', height: '100vh' }}>
      <div style={{ flex: 1, textAlign: 'center', fontFamily: 'AbrilFatface-Regular' }}>
        <h1>Welcome to Pillar Watch</h1>
        <p>Discover the latest in Pillar watches.</p>
        <button onClick={playVideo}>Play Video</button>
      </div>
      <div style={{ flex: 3, display: 'flex', justifyContent: 'flex-end', height: '100%' }}>
        <div style={{ position: 'relative', maxWidth: '100%', height: '100%' }}>
          <video
            ref={videoRef}
            title="Pillar Watch Video"
            style={{ position: 'absolute', width: '100%', height: '100%', top: 0, left: 0 }}
            controls
          >
            <source src={video} type="video/mp4" />
          </video>
        </div>
      </div>
    </div>
  );
};

export default Home;
