import React, { useState, useEffect } from 'react';

export default function PracticeCount({ title, ido }) {
  const [counter, setCounter] = useState(parseInt(ido, 10) || 10);

  useEffect(() => {
    let timer1;

    if (counter > 0) {
      timer1 = setTimeout(() => setCounter(counter - 1), 1000);
    }

    return () => {
      clearTimeout(timer1); // KOMPONENS MEGSZŰNÉSEKOR FUT LE
    };
  }, [counter]);

  return (
    <div>
      <h2>{title}</h2>
      <button disabled={counter === 0} onClick={() => setCounter(parseInt(ido, 10) || 10)}>
        Gondolkodási idő: {counter}
      </button>
    </div>
  );
}
