import React, { useState, useEffect } from "react";

function ProductList() {
  const [data, setData] = useState([]);
  const [index, setIndex] = useState(0);

  useEffect(() => {
    console.log("useEffect");
    fetch('https://dummyjson.com/products')
      .then(response => response.json())
      .then(adat => {
        console.log('ADAT OK');
        if (adat.products && adat.products.length > 0) {
          setData(adat.products);
        }
      })
      .catch(error => console.error('Fetch error:', error));
  }, []);

  const handleNext = () => {
    if (index < data.length - 1) {
      setIndex(index + 1);
    }
  };

  const handlePrev = () => {
    if (index > 0) {
      setIndex(index - 1);
    }
  };

  const handleFirst = () => {
    setIndex(0);
  };

  const handleLast = () => {
    setIndex(data.length - 1);
  };

  const handleThumbnailClick = (thumbIndex) => {
    setIndex(thumbIndex);
  };

  if (!data.length) {
    console.log('Loading...')
    return <div>Loading...</div>;
  }

  const product = data[index];

  return (
    <div>
      <h1>Termékek</h1>
      <div>
        <h2>{product.title}</h2>
        <p>{product.description}</p>
        <p>{product.price}</p>

        <div>
          <img src={product.images[index]} alt={product.title} />
        </div>

        <div>
          {product.images.map((image, idx) => (
            <img
              key={idx}
              src={image}
              alt={product.title}
              className={idx === index ? 'active-thumbnail' : 'thumbnail'}
              onClick={() => handleThumbnailClick(idx)}
            />
          ))}
        </div>

        <div className="pagination">
          <button onClick={handleFirst} disabled={index === 0}>Első</button>
          <button onClick={handlePrev} disabled={index === 0}>Előző</button>
          <button onClick={handleNext} disabled={index === data.length - 1}>Következő</button>
          <button onClick={handleLast} disabled={index === data.length - 1}>Utolsó</button>
        </div>
      </div>
    </div>
  );
}

export default ProductList;
