import React, { useState } from 'react';

const countries = [
  {
    "name": "Hungary",
    "capital": "Budapest",
    "image": "hungary.jpg",
    "description": "Hungary is a country in Central Europe."
  },
  {
    "name": "Germany",
    "capital": "Berlin",
    "image": "germany.jpg",
    "description": "Germany is a country in Western Europe."
  },
  // Tov치bbi orsz치gok...
];

const CountryInfo = ({ country }) => {
  return (
    <div>
      <h2>{country.name}</h2>
      <p>Capital: {country.capital}</p>
      <img src={country.image} alt={country.name} />
      <p>{country.description}</p>
    </div>
  );
};

const CountryDisplay = () => {
  const [page, setPage] = useState(0);
  const countriesPerPage = 1;

  const handleNextPage = () => {
    setPage(page + 1);
  };

  const handlePrevPage = () => {
    setPage(page - 1);
  };

  return (
    <div>
      <CountryInfo country={countries[page]} />
      <button onClick={handlePrevPage} disabled={page === 0}>Previous</button>
      <button onClick={handleNextPage} disabled={page === countries.length - 1}>Next</button>
    </div>
  );
};

export default CountryDisplay;
