import React, { useState, useEffect } from 'react';

function Orszag() {
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('./orszag.json');
        if (!response.ok) {
          throw new Error('Hiba történt a fetchelés során');
        }
        const data = await response.json();
        setCountries(data.orszagok);
      } catch (error) {
        console.error('Hiba történt a fetchelés során:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>Országok</h1>
      <ul>
        {countries.map((country, index) => (
          <li key={index}>
            <h2>{country.orszag}</h2>
            <p>Főváros: {country.fovaros}</p>
            <img src={country.kep} alt={country.orszag} />
            <p>{country.leiras}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Orszag;
