import React, { useState } from 'react';

export default function FilterBox({ cim, lista }) {
  const [selectedItem, setSelectedItem] = useState(null);

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  return (
    <div>
      <h1>{cim}</h1>
      {lista.map((item) => (
        <div
          key={item.id}
          className={`color-item ${item === selectedItem ? "selected" : ""}`}
          onClick={() => handleItemClick(item)}
        >
          {item.szin} {item.meret}
        </div>
      ))}
    </div>
  );
}
