import React, { useState } from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function Navbar() {
    const [showSideBar, setShowSideBar] = useState(false);

    function toggleSideBar() {
        setShowSideBar(!showSideBar);
    }

    return (
        <nav className={`navbar navbar-expand-lg navbar-dark bg-transparent ${showSideBar ? 'active' : ''}`}>
            <div className="container">

                {/* LOGO */}
                <a href="#home" className="navbar-brand fs-4" onClick={toggleSideBar}>
                    <img src="../Images/logo.svg" alt="The Pillar" />
                </a>

                {/* TOGGLE BTN */}
                <button className="navbar-toggler shadow-none border-0" type="button" onClick={toggleSideBar}>
                    <span className="navbar-toggler-icon"></span>
                </button>

                {/* SIDEBAR */}
                <div className={"sidebar offcanvas-start ${showSidebar  ? 'show' : ''}"} tapIndex = '-1' id="offcanvasNavbar" aria-labelledby="offcanvasNavbarLabel">
                </div>

                {/* SIDEBAR HEADER */}
                <div className="offcanvas-header text-white border-bottom">
                    <h5 className="offcanvas-title" id="offcanvasNavbarLabel">The Pillar</h5>
                    <button className="btn-close btn-close-white shadow-none" onClick={toggleSideBar} aria-label="Close"></button>
                </div>

                {/* SIDEBAR BODY */}
                <div className="offcanvas-body d-flex flex-column flex-lg-row p-4">
                {/* NAVBAR LINKS */}
                <ul className="navbar-nav justify-content-center align-item-center fs-5 flex-grow-1 pe-3">
                    <li className="nav-item mx-2">
                        a.nav-link
                    </li>
                </ul>
                </div>

            </div>
        </nav>
    );
}

export default Navbar;
