import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faShoppingCart, faSearch } from '@fortawesome/free-solid-svg-icons';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-svg-core/styles.css';
import { config } from '@fortawesome/fontawesome-svg-core';

config.autoAddCss = false;

const MyNavbar = () => {
  const [isNavbarVisible, setNavbarVisible] = useState(true);

  const handleScroll = () => {
    const currentScrollPos = window.pageYOffset;
    const isScrolledDown = currentScrollPos > 50;
    setNavbarVisible(isScrolledDown);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <Navbar
      expand="lg"
      variant="dark"
      fixed="top"
      className={`${!isNavbarVisible && 'd-none'}`}
    >
      <Container>
        <Navbar.Brand href="#home">Logo</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ml-auto">
            <Nav.Link href="#home">Menu 1</Nav.Link>
            <Nav.Link href="#link">Menu 2</Nav.Link>
            <Nav.Link href="#link">Menu 3</Nav.Link>
            <Nav.Link href="#link">Menu 4</Nav.Link>
            <Nav.Link href="#link">Menu 5</Nav.Link>
            <Nav.Link href="#user">
              <FontAwesomeIcon icon={faUser} />
            </Nav.Link>
            <Nav.Link href="#cart">
              <FontAwesomeIcon icon={faShoppingCart} />
            </Nav.Link>
            <Nav.Link href="#search">
              <FontAwesomeIcon icon={faSearch} />
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default MyNavbar;
