import React, { useState, useEffect } from 'react';
// Correct the import statement to match the exported variable
import WatchesServices from '../../src/WatchesServices';

export default function Watches() {
  const [watches, setWatches] = useState([]);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    "model": '',
    // Egyéb óra adatok...
  });

  useEffect(() => {
    updateWatches();
  }, []);

  const updateWatches = () => {
    WatchesServices.getAll()
      .then(response => setWatches(response.data))
      .catch(error => setError(error.message));
  };

  const handleInputChanges = event => {
    const { name, value } = event.target;
    setFormData(prevFormData => ({ ...prevFormData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.id) {
      // Update logic (use formData.id to identify the watch)
      WatchesServices.update(formData.id, formData)
        .then(() => {
          setFormData({
            "model": '',
            // Egyéb óra adatok...
          });
          updateWatches();
        })
        .catch(error => console.error('Error updating watch:', error));
    } else {
      // Add logic
      WatchesServices.create(formData)
        .then(() => {
          setFormData({
            "model": '',
            // Egyéb óra adatok...
          });
          updateWatches();
        })
        .catch(error => console.error('Error adding watch:', error));
    }
  };

  const handleDelete = (watchId) => {
    WatchesServices.remove(watchId)
      .then(() => {
        updateWatches();
      })
      .catch(error => console.error('Error deleting watch:', error));
  };

  const handleTableRowClick = (watch) => {
    setFormData({
      "model": watch.model,
      // Egyéb óra adatok...
    });
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="model">Óra modell</label>
          <input onChange={handleInputChanges} type="text" name="model" id="model" value={formData.model} />
        </div>
        {/* Egyéb óra adatok... */}
        <input type="submit" value="Mentés" />
      </form>

      <div>
        <h1>Órák ({watches.length} db)</h1>
        {error ? (
          <div>Error fetching data: {error}</div>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Óra modell</th>
                {/* Egyéb táblázat fejléc mezők... */}
                <th>Törlés</th>
              </tr>
            </thead>
            <tbody>
              {watches.map(watch => (
                <tr key={watch.id} onClick={() => handleTableRowClick(watch)}>
                  <td>{watch.model}</td>
                  {/* Egyéb táblázat mezők... */}
                  <td>
                    <button onClick={() => handleDelete(watch.id)}>Törlés</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </>
  );
}
