import React from "react";
import '../lista.css'

export default function Lista({ cim, lista, selectedItem }) {
    return (
        <div>
            <h1>{cim}</h1>
            {lista.map(item => (
                <div
                    key={item.id}
                    className={`color-item ${item === selectedItem ? "selected" : ""}`}
                >
                    {item.szin} {item.meret}
                </div>
            ))}
        </div>
    );
}
