import React, { useState, useEffect } from "react";

function ProductList() {
  const [data, setData] = useState([]);
  const [index, setIndex] = useState(0);

  useEffect(() => {
    fetch('https://dummyjson.com/products')
      .then(response => response.json())
      .then(adat => {
        if (adat.products && adat.products.length > 0) {
          setData(adat.products);
        }
      })
      .catch(error => console.error('Fetch error:', error));
  }, []);

  const handleNext = () => {
    if (index < data.length - 1) {
      setIndex(index + 1);
    }
  };

  const handlePrev = () => {
    if (index > 0) {
      setIndex(index - 1);
    }
  };

  const handleThumbnailClick = () => {
    // Implement custom logic if needed
  };

  if (!data.length) {
    return <div>Loading...</div>;
  }

  const product = data[index];

  return (
    <div>
      <h1>Term√©kek</h1>
      <div>
        <h2>{product.title}</h2>
        <p>{product.description}</p>
        <p>{product.price}</p>

        <div className="image-container">
          <button onClick={handlePrev} disabled={index === 0}>{'<'}</button>
          <img src={product.thumbnail} alt={product.title} />
          <button onClick={handleNext} disabled={index === data.length - 1}>{'>'}</button>
        </div>

        <div>
          {product.images.map((image, idx) => (
            <img
              key={idx}
              src={image}
              alt={product.title}
              className={idx === index ? 'active-thumbnail' : 'thumbnail'}
              onClick={() => handleThumbnailClick(idx)}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default ProductList;
