import React, { useState, useEffect } from 'react';
import FilmService from '../../src/services/FilmService'; 

export default function Filmek() {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({}); 

  useEffect(() => {
    FilmService.getAll()
      .then(response => setData(response.data))
      .catch(error => console.log(error));
  }, []);

  const handleInputChanges = event => {
    const { name, value } = event.target;
    setFormData(prevFormData => ({ ...prevFormData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(JSON.stringify(formData));
    FilmService.create(formData)
      .then(response => {
        console.log(response);
        alert("Sikerült");
        // Call the update function here if needed
      })
      .catch(error => {
        console.error(error);
        alert("Hiba történt a mentés során");
      });
  };

  return (
    <>
      <div>{JSON.stringify(formData)}</div>
      <form onSubmit={handleSubmit}>
        {/* Form inputs */}
      </form>
      <div>
        <h1>Filmek ({data.length} film)</h1>
        {error ? (
          <div>Error fetching data: {error}</div>
        ) : (
          data.map(film => (
            <div key={film.id}>{film.leiras} : {film.cim}</div>
          ))
        )}
      </div>
    </>
  );
}
