import React, { useState, useEffect } from 'react';
import FilmService from '../../src/services/FilmService';

export default function Films() {
  const [films, setFilms] = useState([]);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState(
    {
      "rendezo": '',
      "cime": '',
      "ev": '',
      "nyelv": '',
      "hossz": '',
    });


  useEffect(() => {
    updateFilms();
  }, []);

  const updateFilms = () => {
    FilmService.getAll()
      .then(response => setFilms(response.data))
      .catch(error => setError(error.message));
  };

  // ----------------------------- INPUT CHANGE ----------------------------- //
  const handleInputChanges = event => {
  const { name, value } = event.target;
  setFormData(prevFormData => ({ ...prevFormData, [name]: value }));
};

// ----------------------------- DELETE ----------------------------- //
  const handleSubmit = (e) => {
  e.preventDefault();
  FilmService.create((formData))
    .then(response => {
      console.log(response);
      alert("Sikerült");
      updateFilms();
    })
    .catch(error => {
      console.error(error);
      alert(`Hiba történt a mentés során: ${error.message}`);
      console.log(error.response.data);
    });
};

// ----------------------------- DELETE ----------------------------- //
const handleDelete = (filmId) => {
  FilmService.remove(filmId)
    .then(response => {
      console.log(response);
      alert("Sikeres törlés");
      updateFilms();
    })
    .catch(error => {
      console.error(error);
      alert(`Hiba történt a törlés során: ${error.message}`);
    });
};

const handleTableRowClick = (film) => {
    setFormData({
      "id": film.id,
      "rendezo": film.rendezo,
      "cime": film.cim,
      "ev": film.ev,
      "nyelv": film.nyelv,
      "hossz": film.hossz,
    });
  };

  return (
    <>
      <div>{JSON.stringify(formData)}</div>
      <form onSubmit={handleSubmit}>
      <div>
    <label htmlFor="rendezo">Rendező</label>
    <input onChange={handleInputChanges} type="text" name="rendezo" id="rendezo" />
  </div>
  <div>
    <label htmlFor="cim">Film címe</label>
    <input onChange={handleInputChanges} type="text" name="cim" id="cim" />
  </div>
  <div>
    <label htmlFor="ev">Éve</label>
    <input onChange={handleInputChanges} type="number" name="ev" id="ev" />
  </div>
  <div>
    <label htmlFor="nyelv">Nyelv</label>
    <input onChange={handleInputChanges} type="text" name="nyelv" id="nyelv" />
  </div>
  <div>
    <label htmlFor="hossz">Hossz</label>
    <input onChange={handleInputChanges} type="number" name="hossz" id="hossz" />
  </div>
  <input type="submit" value="Mentés" />
</form>

<div>
        <h1>Filmek ({films.length} film)</h1>
        {error ? (
          <div>Error fetching data: {error}</div>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Rendező</th>
                <th>Film címe</th>
                <th>Éve</th>
                <th>Nyelv</th>
                <th>Hossz</th>
                <th>Törlés</th>
              </tr>
            </thead>
            <tbody>
              {films.map(film => (
                <tr key={film.id}>
                  <td>{film.rendezo}</td>
                  <td>{film.cim}</td>
                  <td>{film.ev}</td>
                  <td>{film.nyelv}</td>
                  <td>{film.hossz}</td>
                  <td>
                    <button onClick={() => handleDelete(film.id)}>Törlés</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </>
  );
}
