import React, { useState, useEffect } from "react";

function ProductList() {
  const [data, setData] = useState([]);
  const [index, setIndex] = useState(0);
  const [imageIndex, setImageIndex] = useState(0);
  const [termek, setTermek] = useState({ title: "", images: [], description: "", price: "" });

  useEffect(() => {
    fetch('https://dummyjson.com/products')
      .then(response => response.json())
      .then(adat => {
        if (adat.products && adat.products.length > 0) {
          setData(adat.products);
          setTermek(adat.products[0]); // Set the initial product
        }
      })
      .catch(error => console.error('Fetch error:', error));
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      if (imageIndex < termek.images.length - 1) {
        setImageIndex(imageIndex + 1);
      } else {
        setImageIndex(0);
      }
    }, 5000);

    return () => clearInterval(interval);
  }, [imageIndex, termek.images]);

  const handleNext = () => {
    if (index < data.length - 1) {
      setIndex(index + 1);
      setTermek(data[index + 1]); // Update the displayed product
      setImageIndex(0); // Reset the image index
    }
  };

  const handlePrev = () => {
    if (index > 0) {
      setIndex(index - 1);
      setTermek(data[index - 1]); // Update the displayed product
      setImageIndex(0); // Reset the image index
    }
  };

  const handleFirst = () => {
    setIndex(0);
    setTermek(data[0]); // Update the displayed product
    setImageIndex(0); // Reset the image index
  };

  const handleLast = () => {
    setIndex(data.length - 1);
    setTermek(data[data.length - 1]); // Update the displayed product
    setImageIndex(0); // Reset the image index
  };

  return (
    <div>
      <h1>Termékek</h1>
      {data.length > 0 && (
        <>
          <h2>{termek.title}</h2>
          <img src={termek.images[imageIndex]} alt={termek.title} />
          <div>
            <button onClick={handlePrev} disabled={index === 0}>Előző kép</button>
            <button onClick={handleNext} disabled={index === data.length - 1}>Következő kép</button>
          </div>
          <p>{termek.description}</p>
          <p>{termek.price}</p>
          <div className="pagination">
            <button onClick={handleFirst} disabled={index === 0}>Első</button>
            <button onClick={handlePrev} disabled={index === 0}>Előző</button>
            <button onClick={handleNext} disabled={index === data.length - 1}>Következő</button>
            <button onClick={handleLast} disabled={index === data.length - 1}>Utolsó</button>
          </div>
        </>
      )}
    </div>
  );
}

export default ProductList;
