import React, { useState } from 'react';
import './Lista.css'; // Fix the import path for the CSS file

export default function Lista({ cim, lista }) {
    const [selectedItem, setSelectedItem] = useState(initialSelectedItem);

    const handleItemClick = (item) => {
        setSelectedItem(item === selectedItem ? null : item);
    };

    return (
        <div>
            <h1>{cim}</h1>
            {lista.map(item => (
                <div
                    key={item.id}
                    className={`color-item ${item === selectedItem ? "selected" : ""}`}
                    onClick={() => handleItemClick(item)}
                >
                    {item.szin} {item.meret}
                </div>
            ))}
        </div>
    );
}
