import React, { useState, useEffect } from 'react';
import FilmService from '../../src/services/FilmService'; 
import { json } from 'stream/consumers';


export default function Filmek() {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState([]); 

  useEffect(() => {
    // fetch('http://localhost:8080/filmek')
    //   .then(response => response.json())
    //   .then(adat => setData(adat))
    //   .catch(error => setError(error.message));
    FilmService.getAll()
    .then(response => setData(response.data))
    .catch(error => console.log(error))

    }, []);

    const handleInputChanges = event => {
     const { name, value } = event.target;
  setFormData({ ...formData, [name]: value });
}


  return (
    <>
    <div>{JSON.stringify}</div>
    <form action="">
    <div>
      <label htmlFor="">Rendező</label>
      <input onChange={handleInputChanges} type="text" name='rendezo'/>
    </div>
    <div>
      <label htmlFor="">Film címe</label>
      <input onChange={handleInputChanges} type="text" name='cime'/>
    </div>
    <div>
      <label htmlFor="">Éve</label>
      <input onChange={handleInputChanges} type="number" name='eve'/>
    </div>
    <div>
      <label htmlFor="">Nyelv</label>
      <input  onChange={handleInputChanges} type="text" name='nyelv'/>
    </div>
    <div>
      <label htmlFor="">Hossz</label>
      <input onChange={handleInputChanges} type="number" name='hossz'/>
    </div>
    <input type="submit" value='Mentés'/>
      
    </form>
    <div>
      <h1>Filmek ({data.length} film)</h1>
      {error ? (
        <div>Error fetching data: {error}</div>
      ) : (
        data.map(film => (
          <div key={film.id}>{film.leiras} : {film.cim}</div>
        ))
      )}
    </div>
    </>
  );
}
