import React, { useState, useEffect } from "react";

function ProductList() {
  const [data, setData] = useState([]);
  const [index, setIndex] = useState(0);
  const [arrowDirection, setArrowDirection] = useState("next"); // "prev", "next"

  useEffect(() => {
    console.log("useEffect");
    fetch('https://dummyjson.com/products')
      .then(response => response.json())
      .then(adat => {
        console.log('ADAT OK');
        if (adat.products && adat.products.length > 0) {
          setData(adat.products);
        }
      })
      .catch(error => console.error('Fetch error:', error));
  }, []);

  const handleNext = () => {
    if (index < data.length - 1) {
      setIndex(index + 1);
      setArrowDirection("next");
    }
  };

  const handlePrev = () => {
    if (index > 0) {
      setIndex(index - 1);
      setArrowDirection("prev");
    }
  };

  const handleFirst = () => {
    setIndex(0);
    setArrowDirection("next");
  };

  const handleLast = () => {
    setIndex(data.length - 1);
    setArrowDirection("prev");
  };

  let termek = data[index];

  return (
    <div>
      <h1>Termékek</h1>
      {data.length > 0 && (
        <>
          <h2>{termek.title}</h2>
          <p>{termek.description}</p>
          <p>{termek.price}</p>
          {termek.images && termek.images.length > 0 && (
            <div style={{ position: "relative" }}>
              {arrowDirection === "prev" && (
                <button
                  style={{ position: "absolute", left: 0 }}
                  onClick={handlePrev}
                  disabled={index === 0}
                >
                  {"<"}
                </button>
              )}
              <img
                src={termek.images[0]}
                alt={termek.title}
                style={{ maxWidth: "100%" }}
              />
              {arrowDirection === "next" && (
                <button
                  style={{ position: "absolute", right: 0 }}
                  onClick={handleNext}
                  disabled={index === data.length - 1}
                >
                  {">"}
                </button>
              )}
            </div>
          )}

          <div className="pagination">
            <button onClick={handleFirst} disabled={index === 0}>
              Első
            </button>
            <button onClick={handlePrev} disabled={index === 0}>
              Előző
            </button>
            <button onClick={handleNext} disabled={index === data.length - 1}>
              Következő
            </button>
            <button onClick={handleLast} disabled={index === data.length - 1}>
              Utolsó
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default ProductList;
