import React, { useState, useEffect } from 'react';

const Orszag = () => {
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    fetch('/orszag.json')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Hiba történt a fájl betöltésekor');
          return response.json();
        }
        return response.json();
      })
      .then((data) => {
        setCountries(data.orszagok);
      })
      .catch((error) => {
        console.error('Hiba történt:', error);
      });
  }, []);

  return (
    <div>
      <h1>Országok listája</h1>
      <ul>
        {countries.map((orszag, index) => (
          <li key={index}>
            <h3>{orszag.orszag}</h3>
            <p><strong>Főváros:</strong> {orszag.fovaros}</p>
            <p>{orszag.leiras}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Orszag;
