import React, { useState, useEffect } from "react";

function ProductList() {
  const [data, setData] = useState([]);
  const [index, setIndex] = useState(0);
  const [images, setImages] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    fetch('https://dummyjson.com/products')
      .then(response => response.json())
      .then(adat => {
        if (adat.products && adat.products.length > 0) {
          setData(adat.products);
        }
      })
      .catch(error => console.error('Fetch error:', error));
  }, []);

  useEffect(() => {
    fetch('https://dummyjson.com/images')
      .then(response => response.json())
      .then(data => {
        if (data.images && data.images.length > 0) {
          setImages(data.images);
        }
      })
      .catch(error => console.error('Fetch error:', error));
  }, []);

  const handleNext = () => {
    if (currentIndex < images.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const handlePrev = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  const handleFirst = () => {
    setCurrentIndex(0);
  };

  const handleLast = () => {
    setCurrentIndex(images.length - 1);
  };

  let termek = data[index];

  return (
    <div>
      <h1>Termékek</h1>
      {data.length > 0 && (
        <>
          <h2>{termek.title}</h2>
          <p>{termek.description}</p>
          <p>{termek.price}</p>

          <div className="pagination">
            <button onClick={handleFirst} disabled={index === 0}>Első</button>
            <button onClick={handlePrev} disabled={index === 0}>Előző</button>
            <button onClick={handleNext} disabled={index === data.length - 1}>Következő</button>
            <button onClick={handleLast} disabled={index === data.length - 1}>Utolsó</button>
          </div>
        </>
      )}

      <h1>Képgaléria</h1>
      {images.length > 0 && (
        <>
          <img src={images[currentIndex]} alt={`Kép ${currentIndex + 1}`} />
          <div className="pagination">
            <button onClick={handleFirst} disabled={currentIndex === 0}>Első</button>
            <button onClick={handlePrev} disabled={currentIndex === 0}>Előző</button>
            <button onClick={handleNext} disabled={currentIndex === images.length - 1}>Következő</button>
            <button onClick={handleLast} disabled={currentIndex === images.length - 1}>Utolsó</button>
          </div>
        </>
      )}
    </div>
  );
}

export default ProductList;
