import React, { useState, useEffect } from 'react';
import FilmService from '../../src/services/FilmService'; 

export default function Filmek() {
  const [data, setData] = useState([]);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({}); 


  function update(){
    useEffect(() => {
    FilmService.getAll()
      .then(response => setData(response.data))
      .catch(error => console.log(error));
  }, []);
  }
  

  const handleInputChanges = event => {
    const { name, value } = event.target;
    setFormData((prevFormData) => ({ ...prevFormData, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(JSON.stringify(formData));
    FilmService.create(formData)
    .then(response => {
      console.log(response);
      alert("Sikerült");
      update();
    })
    .catch(error => {
      console.error(error);
      alert("Hiba történt a mentés során");
    });
};

  return (
    <>
    <div>{JSON.stringify(formData)}</div>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="rendezo">Rendező</label>
          <input onChange={handleInputChanges} type="text" name='rendezo' id='rendezo' />
        </div>
        <div>
          <label htmlFor="cime">Film címe</label>
          <input onChange={handleInputChanges} type="text" name='cime' id='cime' />
        </div>
        <div>
          <label htmlFor="eve">Éve</label>
          <input onChange={handleInputChanges} type="number" name='eve' id='eve' />
        </div>
        <div>
          <label htmlFor="nyelv">Nyelv</label>
          <input onChange={handleInputChanges} type="text" name='nyelv' id='nyelv' />
        </div>
        <div>
          <label htmlFor="hossz">Hossz</label>
          <input onChange={handleInputChanges} type="number" name='hossz' id='hossz' />
        </div>
        <input type="submit" value='Mentés' />
      </form>
      <div>
        <h1>Filmek ({data.length} film)</h1>
        {error ? (
          <div>Error fetching data: {error}</div>
        ) : (
          data.map(film => (
            <div key={film.id}>{film.leiras} : {film.cim}</div>
          ))
        )}
      </div>
    </>
  );
}
